import React, { useState, useEffect } from 'react';
import { rentAPI, propertyAPI, tenantAPI } from '../services/api';
import Navbar from '../components/Navbar';
import './CRUDPages.css';

const RentEntries = () => {
  const [rentEntries, setRentEntries] = useState([]);
  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [formData, setFormData] = useState({
    property: '',
    tenant: '',
    rentAmount: '',
    dueDate: '',
    status: 'pending',
    paymentMethod: 'other',
    lateFee: 0,
    notes: ''
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [rentRes, propRes, tenantRes] = await Promise.all([
        rentAPI.getAll(),
        propertyAPI.getAll(),
        tenantAPI.getAll()
      ]);
      setRentEntries(rentRes.data);
      setProperties(propRes.data);
      setTenants(tenantRes.data);
    } catch (error) {
      console.error('Failed to fetch data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await rentAPI.update(editingId, formData);
      } else {
        await rentAPI.create(formData);
      }
      resetForm();
      fetchData();
    } catch (error) {
      alert('Operation failed: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleMarkAsPaid = async (id) => {
    try {
      await rentAPI.markAsPaid(id, 'other');
      fetchData();
    } catch (error) {
      alert('Failed to mark as paid: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleEdit = (entry) => {
    setFormData({
      property: entry.property._id || entry.property,
      tenant: entry.tenant._id || entry.tenant,
      rentAmount: entry.rentAmount,
      dueDate: entry.dueDate.split('T')[0],
      status: entry.status,
      paymentMethod: entry.paymentMethod,
      lateFee: entry.lateFee,
      notes: entry.notes
    });
    setEditingId(entry._id);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this rent entry?')) {
      try {
        await rentAPI.delete(id);
        fetchData();
      } catch (error) {
        alert('Delete failed: ' + (error.response?.data?.message || error.message));
      }
    }
  };

  const resetForm = () => {
    setFormData({
      property: '',
      tenant: '',
      rentAmount: '',
      dueDate: '',
      status: 'pending',
      paymentMethod: 'other',
      lateFee: 0,
      notes: ''
    });
    setEditingId(null);
    setShowForm(false);
  };

  const calculateTotal = () => {
    return rentEntries
      .filter(entry => entry.status === 'pending' || entry.status === 'overdue')
      .reduce((sum, entry) => sum + entry.rentAmount, 0);
  };

  return (
    <>
      <Navbar />
      <div className="page-container">
        <div className="page-header">
          <div>
            <h1>Rent Entries</h1>
            <p className="total-pending">Total Pending: ${calculateTotal().toFixed(2)}</p>
          </div>
          <button onClick={() => setShowForm(!showForm)} className="add-btn">
            {showForm ? 'Cancel' : '+ Add Rent Entry'}
          </button>
        </div>

        {showForm && (
          <div className="form-card">
            <h2>{editingId ? 'Edit Rent Entry' : 'Add New Rent Entry'}</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label>Property *</label>
                  <select
                    name="property"
                    value={formData.property}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Property</option>
                    {properties.map(prop => (
                      <option key={prop._id} value={prop._id}>
                        {prop.address}, {prop.city}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label>Tenant *</label>
                  <select
                    name="tenant"
                    value={formData.tenant}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Tenant</option>
                    {tenants.map(tenant => (
                      <option key={tenant._id} value={tenant._id}>
                        {tenant.name}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Rent Amount ($) *</label>
                  <input
                    type="number"
                    name="rentAmount"
                    value={formData.rentAmount}
                    onChange={handleChange}
                    step="0.01"
                    min="0"
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Due Date *</label>
                  <input
                    type="date"
                    name="dueDate"
                    value={formData.dueDate}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Status</label>
                  <select name="status" value={formData.status} onChange={handleChange}>
                    <option value="pending">Pending</option>
                    <option value="paid">Paid</option>
                    <option value="overdue">Overdue</option>
                    <option value="partial">Partial</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Payment Method</label>
                  <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
                    <option value="cash">Cash</option>
                    <option value="check">Check</option>
                    <option value="bank_transfer">Bank Transfer</option>
                    <option value="online">Online</option>
                    <option value="other">Other</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Late Fee ($)</label>
                  <input
                    type="number"
                    name="lateFee"
                    value={formData.lateFee}
                    onChange={handleChange}
                    step="0.01"
                    min="0"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Notes</label>
                <textarea
                  name="notes"
                  value={formData.notes}
                  onChange={handleChange}
                  rows="3"
                />
              </div>

              <button type="submit" className="submit-btn">
                {editingId ? 'Update Entry' : 'Add Entry'}
              </button>
            </form>
          </div>
        )}

        <div className="items-grid">
          {loading ? (
            <div className="loading-container">
              <div className="spinner"></div>
              <p>Loading rent entries...</p>
            </div>
          ) : rentEntries.length === 0 ? (
            <p className="no-items">No rent entries found. Add your first entry above.</p>
          ) : (
            rentEntries.map((entry) => (
              <div key={entry._id} className={`item-card rent-${entry.status}`}>
                <div className="item-header">
                  <h3>${entry.rentAmount.toFixed(2)}</h3>
                  <span className={`badge ${entry.status}`}>{entry.status}</span>
                </div>
                <div className="item-body">
                  <p><strong>Property:</strong> {entry.property?.address || 'N/A'}</p>
                  <p><strong>Tenant:</strong> {entry.tenant?.name || 'N/A'}</p>
                  <p><strong>Due Date:</strong> {new Date(entry.dueDate).toLocaleDateString()}</p>
                  {entry.paidDate && (
                    <p><strong>Paid On:</strong> {new Date(entry.paidDate).toLocaleDateString()}</p>
                  )}
                  {entry.lateFee > 0 && <p><strong>Late Fee:</strong> ${entry.lateFee.toFixed(2)}</p>}
                  {entry.notes && <p className="description">{entry.notes}</p>}
                </div>
                <div className="item-actions">
                  {entry.status === 'pending' && (
                    <button onClick={() => handleMarkAsPaid(entry._id)} className="paid-btn">
                      Mark Paid
                    </button>
                  )}
                  <button onClick={() => handleEdit(entry)} className="edit-btn">Edit</button>
                  <button onClick={() => handleDelete(entry._id)} className="delete-btn">Delete</button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </>
  );
};

export default RentEntries;
